// sourceBadgeColor returns a Tailwind color class for the provider source badge.
func sourceBadgeColor(source string) string {
	switch source {
	case "fanarttv":
		return "bg-blue-100 dark:bg-blue-900 text-blue-800 dark:text-blue-200"
	case "audiodb":
		return "bg-green-100 dark:bg-green-900 text-green-800 dark:text-green-200"
	case "discogs":
		return "bg-orange-100 dark:bg-orange-900 text-orange-800 dark:text-orange-200"
	case "musicbrainz":
		return "bg-amber-100 dark:bg-amber-900 text-amber-800 dark:text-amber-200"
	case "duckduckgo":
		return "bg-purple-100 dark:bg-purple-900 text-purple-800 dark:text-purple-200"
	default:
		return "bg-gray-100 dark:bg-gray-700 text-gray-800 dark:text-gray-200"
	}
}

// isLowResolution returns true if the image dimensions indicate low quality for the given type.
func isLowResolution(w, h int, imageType string) bool {
	if w == 0 || h == 0 {
		return false
	}
	switch imageType {
	case "banner":
		return w < 758 || h < 140
	case "fanart", "background":
		return w < 960 || h < 540
	case "logo", "hdlogo":
		return w < 400 || h < 155
	default: // thumb, poster, folder
		return w < 500 || h < 500
	}
}

